const pageWrapper = {
  fontFamily: "'Segoe UI', sans-serif",
  minHeight: "100vh",
  background: "url('https://images.unsplash.com/photo-1605902711622-cfb43c4437d3') center/cover no-repeat",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  padding: "2rem",
};

const glassCard = {
  background: "rgba(255, 255, 255, 0.85)",
  backdropFilter: "blur(10px)",
  borderRadius: "20px",
  padding: "2.5rem 3rem",
  maxWidth: "800px",
  width: "100%",
  boxShadow: "0 8px 24px rgba(0, 0, 0, 0.2)",
};

const titleStyle = {
  fontSize: "2rem",
  fontWeight: "700",
  color: "#0d47a1",
  marginBottom: "2rem",
  textAlign: "center",
};

const ticketListStyle = {
  display: "flex",
  flexDirection: "column",
  gap: "1.5rem",
};

const ticketItemStyle = {
  background: "#fefefe",
  padding: "1.2rem",
  borderRadius: "12px",
  borderLeft: "6px solid #0d47a1",
  boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
};

const ticketHeaderStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
};

const ticketMetaStyle = {
  marginTop: "0.5rem",
  color: "#666",
  fontSize: "0.95rem",
};

const statusStyle = (statut) => {
  let color;
  switch (statut) {
    case "Ouvert":
      color = "#ff9800";
      break;
    case "En cours":
      color = "#2196f3";
      break;
    case "Résolu":
      color = "#4caf50";
      break;
    default:
      color = "#999";
  }
  return {
    padding: "0.4rem 0.8rem",
    backgroundColor: color,
    color: "#fff",
    borderRadius: "8px",
    fontSize: "0.8rem",
    fontWeight: "600",
  };
};
